mapper.select:
    select 

    ,

    ,(select count(*) from tbl_article where userId = #{userid,jdbcType=INTEGER} and isValid = 1) as pageCount 

    from tbl_article t
    where userId = #{userid,jdbcType=INTEGER}
    and isValid = 1
    order by t.createTime DESC


			limit #{limitSt},#{pageSize}


			limit #{defaultPageSize,jdbcType=INTEGER}

    select 

    ,

     ,

    ,(select count(*)   from tbl_article t  JOIN tbl_collect myc ON t.id = myc.objectId   
	    where   t.isValid = 1 
	    AND myc.userId = #{myCollect.userid,jdbcType=INTEGER}
	    AND myc.collectType = #{myCollect.collecttype,jdbcType=VARCHAR}
	    AND myc.actionType = #{myCollect.actionType,jdbcType=INTEGER}) as pageCount
    from tbl_article t  JOIN tbl_collect myc ON t.id = myc.objectId   
    where   t.isValid = 1 
    AND myc.userId = #{myCollect.userid,jdbcType=INTEGER}
    AND myc.collectType = #{myCollect.collecttype,jdbcType=VARCHAR}
    AND myc.actionType = #{myCollect.actionType,jdbcType=INTEGER}
    order by myc.collTime DESC


			limit #{limitSt},#{pageSize}


			limit #{defaultPageSize,jdbcType=INTEGER}

select
,
,u.userName as "objCreator.userName",u.headIcon as "objCreator.headIcon",u.userid as "objCreator.userid" ,(SELECT group_concat(tlk.userid) FROM tbl_article_isliked tlk WHERE tlk.articleId = #{id,jdbcType=INTEGER})AS likedIds, (SELECT group_concat(tcl.userid) FROM tbl_collect tcl WHERE tcl.collectType = "tbl_article" AND tcl.actionType = 1 AND tcl.objectId = #{id,jdbcType=INTEGER}) AS collectIds from tbl_article t left join tbl_user u on t.userid = u.userid where t.ID = #{id,jdbcType=INTEGER}
