mapper.select:
select
, u.userName as userName, u.headIcon as userHeadIcon, uc.userName as commentbyName, uc.headIcon as commentbyHeadIcon ,(select GROUP_CONCAT(lik.userid) FROM tbl_book_isliked lik where lik.commId =t.ID ) as likedIds
from tbl_books_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid
where 1 = 1
and t.isdelete = 0
AND bookID = #{bookid,jdbcType=INTEGER}
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}

select count(*) from tbl_books_share t LEFT JOIN tbl_user u on t.userID =u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid where 1 = 1
and t.isdelete = 0
AND bookID = #{bookid,jdbcType=INTEGER}
AND bookID = #{bookid,jdbcType=INTEGER}

select ,
from tbl_books_share t join tbl_books b on t.bookID = b.ID where 1= 1
and t.isdelete = 0 AND t.ID = #{id,jdbcType=INTEGER}

select
from tbl_books_share where ID = #{id,jdbcType=INTEGER}
and isdelete = 0

mapper.update:
update tbl_books_share
set isdelete = 1
