mapper.select:
    select 

    ,
    ,
    (SELECT group_concat(tcl.userid) FROM tbl_cpnews_isliked tcl WHERE tcl.newsId=#{id,jdbcType=INTEGER})AS likedIds
    from tbl_company_news t
    where t.compId = #{compid,jdbcType=INTEGER}
    and t.isValid=1 ORDER BY t.isTop,t.topTime,t.updateTime


				limit #{limitSt},#{pageSize}


				limit #{defaultPageSize,jdbcType=INTEGER}
