mapper.select:
select
, u.userName as userName, u.headIcon as userHeadIcon, uc.userName as commentbyName, uc.headIcon as commentbyHeadIcon
from tbl_courses_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid
where 1=1
and t.isdelete = 0 and u.isValid = 1
and t.coursesId = #{coursesid,jdbcType=INTEGER}
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}

select count(*) from tbl_courses_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid where 1=1
and t.isdelete = 0 and u.isValid = 1
and t.coursesId = #{coursesid,jdbcType=INTEGER}

select
from tbl_courses_share where ID = #{id,jdbcType=INTEGER}
and isdelete = 0

mapper.update:
update tbl_courses_share
set isdelete = 1
where ID = #{id,jdbcType=INTEGER}
