mapper.select:
  	select * from (
	SELECT 	bk.ID AS id,	bk.bookname AS title,	bks.createTime AS createtime,	'tbl_books' AS type FROM	(select bks.bookid as bookID,max(bks.createtime) as  createTime from tbl_books_share bks where  bks.userID = #{userid , jdbcType=INTEGER} group by bks.bookid) bks,tbl_books bk WHERE	bks.bookID = bk.ID
	UNION all
	select ds.ID as id ,ds.title as title, ds.createTime as createtime,'tbl_docs' as type   from tbl_docs ds where   ds.isValid='1' and ds.isPublic='1' and ds.userId = #{userid , jdbcType=INTEGER}
	UNION all
	select tps.ID as id ,tps.title as title, tps.createTime as createtime,'tbl_topics' as type   from tbl_topics tps where   tps.isValid='1' and tps.isPublic='1' and tps.createPerson=#{userid , jdbcType=INTEGER}
	UNION all
	select cs.ID as id ,cs.coursesName as title, cs.createTime as createtime,'tbl_courses' as type   from tbl_courses cs where  cs.isValid='1'  and cs.userId = #{userid , jdbcType=INTEGER}
	UNION all
	select art.ID as id ,art.title as title, art.createTime as createtime,'tbl_article' as type   from tbl_article art where  art.isValid='1'  and art.userId = #{userid , jdbcType=INTEGER} and art.ispublic='1'
	UNION all
	select sit.ID as id ,sit.title as title, sit.createTime as createtime,'tbl_sites' as type   from tbl_sites sit where sit.isValid='1'  and sit.userId = #{userid , jdbcType=INTEGER}  
	ORDER BY createTime desc
	) as ta



			limit #{limitSt},#{pageSize}


			limit #{defaultPageSize,jdbcType=INTEGER}

  	select * from (select sum(countRs) as pageCount from (
		select count(DISTINCT bk.ID ) as countRs from tbl_books_share bks,tbl_books bk where bks.bookID = bk.ID   and bks.userID = #{userid , jdbcType=INTEGER}
		UNION all
		select count(*)  as countRs  from tbl_docs ds where   ds.isValid='1' and ds.isConverter = '1' and ds.isPublic='1' and ds.userId = #{userid , jdbcType=INTEGER}
		UNION all
		select count(*)  as countRs from tbl_topics tps where  tps.isValid='1' and tps.isPublic='1' and tps.createPerson=#{userid , jdbcType=INTEGER}
		UNION all
		select count(*)  as countRs from tbl_courses cs where   cs.isValid='1'  and cs.userId = #{userid , jdbcType=INTEGER}
		UNION all
		select count(*)  as countRs from tbl_article art where  art.isValid='1'  and art.userId = #{userid , jdbcType=INTEGER} and art.ispublic='1'
		UNION all
		select count(*)  as countRs from tbl_sites sit where   sit.isValid='1'  and sit.userId = #{userid , jdbcType=INTEGER}  ) as tb) aa

    select 
    id,  ${tableColumn} as title ,createtime

    from ${tableName}
    where ${tableColumn2} = #{userid,jdbcType=INTEGER} and isValid=1



		and	isPublic = 1




		and	isConverter = 1


    order by createTime desc


			limit #{limitSt},#{pageSize}


			limit #{defaultPageSize,jdbcType=INTEGER}

SELECT t.bookname as title,t.ID as id ,bs.createTime as createTime ,(select count(*) FROM tbl_books_share bs LEFT JOIN tbl_books t ON bs.bookID = t.ID WHERE bs.userID = #{userid,jdbcType=INTEGER} AND bs.commentby IS NULL) as pageCount FROM tbl_books_share bs LEFT JOIN tbl_books t ON bs.bookID = t.ID WHERE bs.userID = #{userid,jdbcType=INTEGER} AND bs.commentby IS NULL and t.isValid=1 ORDER BY bs.createTime DESC
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}
