mapper.select:
select
, u.userName as userName, u.headIcon as userHeadIcon, u.usertype as usertype, uc.userName as commentbyName, uc.headIcon as commentbyHeadIcon
from tbl_sites_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid
where 1=1
and t.isdelete = 0
and t.siteID = #{siteid,jdbcType=INTEGER}
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}

select count(*) from tbl_sites_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid where 1=1
and t.isdelete = 0
and t.siteID = #{siteid,jdbcType=INTEGER}

select
from tbl_sites_share where ID = #{id,jdbcType=INTEGER}
and isdelete = 0

mapper.update:
update tbl_sites_share
set isdelete = 1
where ID = #{id,jdbcType=INTEGER}
