mapper.select:
select
,
from tbl_user t left join tbl_operationSum p on t.userId = p.userid where t.userId = #{userID,jdbcType=INTEGER}

 select 
	u.userId,u.userName,u.headIcon 
 from tbl_attention at,tbl_user u where at.collType='${collType}' and at.objectId = ${objectId} and u.userId = at.userId LIMIT 20 ;

select
,
,(select group_concat(userid) from tbl_attention where objectId = at.userid and colltype='tbl_user') as fansIds ,(select COUNT( at.userid) from tbl_attention at RIGHT join tbl_user t on t.userId = at.userId left join tbl_operationSum p on t.userId = p.userid where at.collType='tbl_user' and at.objectId = #{userid,jdbcType=INTEGER} ) as pageCount
from tbl_attention at RIGHT join tbl_user t on t.userId = at.userId left join tbl_operationSum p on t.userId = p.userid where at.collType='tbl_user' and at.objectId = #{userid,jdbcType=INTEGER}
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}

select
,
,(select group_concat(userid) from tbl_attention where objectId = t.userid and colltype='tbl_user') as fansIds ,(select COUNT(at.objectId) from tbl_user t left join tbl_attention at on t.userId = at.objectId left join tbl_operationSum p on at.objectId = p.userid where at.collType='tbl_user' and at.userid = #{userid,jdbcType=INTEGER} ) as pageCount
from tbl_user t left join tbl_attention at on t.userId = at.objectId left join tbl_operationSum p on at.objectId = p.userid where at.collType='tbl_user' and at.userid = #{userid,jdbcType=INTEGER}
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}

select COUNT(at.objectId) from  tbl_user t left join tbl_attention at on t.userId = at.objectId left join tbl_operationSum p on at.objectId = p.userid
where at.collType='tbl_user'
and at.userid = #{userid,jdbcType=INTEGER}
