mapper.select:
select
, u.userName as userName, u.headIcon as userHeadIcon, uc.userName as commentbyName, uc.headIcon as commentbyHeadIcon
from tbl_article_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid
where 1=1
and t.isdelete = 0
AND t.userID = #{userid,jdbcType=INTEGER}
and t.articleID = #{articleid,jdbcType=INTEGER}
limit #{limitSt},#{pageSize}
limit #{defaultPageSize,jdbcType=INTEGER}

select count(*) from tbl_article_share t LEFT JOIN tbl_user u on t.userID = u.userid LEFT JOIN tbl_user uc on t.commentby = uc.userid where 1=1
and t.isdelete = 0
AND t.userID = #{userid,jdbcType=INTEGER}
and t.articleID = #{articleid,jdbcType=INTEGER}

select
from tbl_article_share where ID = #{id,jdbcType=INTEGER}
and isdelete = 0

mapper.update:
update tbl_article_share
set isdelete = 1
where ID = #{id,jdbcType=INTEGER}
