mapper.select:
	select

	,


from tbl_topics t
where 1 =1
AND t.createPerson = #{createperson,jdbcType=INTEGER}

AND t.isValid = 1
ORDER BY t.createTime DESC


		limit #{limitSt},#{pageSize}


		limit #{defaultPageSize,jdbcType=INTEGER}

mapper.update:
